inline int64_t calc_crc64(const char *data, const int64_t len, int64_t init_crc = 0)
{
  int64_t crc = init_crc;
  if (NULL == data || len <= 0) {
    crc = NOT_CHECK_SUM;
  } else {
    const char *ptr = data;
    int64_t count = len;
    while (count-- > 0) {
      uint table_index = ((uint)(crc >> 56) ^ * ptr++) & 0xFF;
      crc = crc64_tab[table_index] ^ (crc << 8);
    }
  }
  return crc;
}

